	public static void sysArr(int[] arr) {
		System.out.print("[");
		for(int i =0; i<arr.length; i++) {
			if(i == arr.length - 1) {
				System.out.print(arr[i] + "]");
				break;
			}
			System.out.print(arr[i]+", ");
		}
	}

